const geminiResponses = $input.all();
const originalArticles = $input.all(1);

const results = [];

for (let i = 0; i < geminiResponses.length; i++) {
  const geminiItem = geminiResponses[i].json;
  const originalItem = originalArticles[i]?.json || {};

  const text = geminiItem?.candidates?.[0]?.content?.parts?.[0]?.text || "";

  let jsonText = text.trim();
  if (jsonText.startsWith("```json")) {
    jsonText = jsonText.replace(/^```json\s*/, "").replace(/```$/, "").trim();
  }

  try {
    const parsed = JSON.parse(jsonText);

    results.push({
      json: {
        title: parsed.title || originalItem.title || "Untitled",
        summary: parsed.summary || "[Missing summary]",
        content: parsed.content || "[Missing content]",
        jokes: parsed.jokes || "[Missing joke]",
        quote: parsed.quote || "[Missing quote]",
        questions: parsed.questions || [],
        imageIdeas: parsed.imageIdeas || [],
        source: parsed.source || originalItem.link || "",
        image: parsed.image || originalItem.image || "",
        timestamp: new Date().toISOString()
      }
    });
  } catch (err) {
    console.warn(`Skipping item ${i + 1}: Invalid JSON – ${err.message}`);
    results.push({
      json: {
        title: "[Invalid JSON]",
        summary: "[Gemini returned invalid JSON]",
        content: text,
        image: originalItem.image || "",
        source: originalItem.link || "",
        timestamp: new Date().toISOString()
      }
    });
  }
}

return results;
